b:switch.script:
document.getElementById("urlForm").addEventListener("submit", function(event) {
    event.preventDefault();

    // Get the custom value from the input field
    const customValue = document.getElementById("customValue").value;

    // Generate the modified URL
    const baseUrl = "/search?q=";
    const viewParam = "&view=youtube";
    const generatedUrl = baseUrl + customValue + viewParam;

    // Display the generated URL as a clickable link
    const generatedUrlElement = document.getElementById("generatedUrl").querySelector("a");
    generatedUrlElement.href = generatedUrl;
    generatedUrlElement.textContent = generatedUrl;

    // Enable the copy button
    const copyButton = document.getElementById("copyButton");
    copyButton.disabled = false;

    // Add click event listener to the copy button
    copyButton.addEventListener("click", function() {
        // Copy the generated URL to the clipboard
        const textArea = document.createElement("textarea");
        textArea.value = generatedUrl;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand("copy");
        document.body.removeChild(textArea);

        // Inform the user that the URL has been copied
        alert("URL has been copied to clipboard!");
    });
});

body.script:

// Function to close ads
function closeAds() {
    var adsxElement = document.getElementById("adsx");
    if (adsxElement) {
        adsxElement.style.display = "none";
    }
}

// Function to show ads
function showAds() {
    var adsxElement = document.getElementById("adsx");
    if (adsxElement) {
        adsxElement.style.display = "block";
    }
}
